import React, { useState } from "react";
import axios from "axios";
import FormData from "form-data";
// import Timeline, { click, setClick } from "./Timeline";
function Upload() {
  const [title, setTitle] = useState("");
  const [category, setCategory] = useState("");
  const [file, setFile] = useState("");
  const [DataRecieved, setDataRecieved] = useState({});
  // const [click, setClick] = useState(false);

  let submit = (e) => {
    e.preventDefault();
    let data = new FormData();
    data.append("filename", file);
    data.append("title", title);
    data.append("category", category);
    let uname = localStorage.getItem("uname");
    data.append("uname", uname);

    axios
      .post("http://localhost:3001/timeline", data, {
        headers: {
          "Content-Type": "multipart/form-data;",
        },
      })
      .then((response) => {
        console.log(response);
        setDataRecieved(response);
      });
    // console.log(pass);



    useEffect(() => {
      effect
      return () => {
        cleanup
      }
    }, [input])(() => {
      const id = localStorage.getItem("uname");
      axios
        .post("http://localhost:3001/getupload", { uname: id })
        .then((response) => {
          console.log(response);
        });
    }, []);




  };
  console.log("This is ", DataRecieved.data);

  return (
    <div className="uploadform">
      <form>
        <h2>Upload Image</h2>
        <br></br>
        <br></br>
        <h4>
          Description:&nbsp;
          <input
            type="text"
            name="title"
            onChange={(evt) => {
              setTitle(evt.target.value);
            }}
          ></input>
        </h4>
        <br></br>
        <h4>
          Category: &nbsp;&nbsp;&nbsp;&nbsp;
          <input
            type="text"
            name="category"
            onChange={(evt) => {
              setCategory(evt.target.value);
            }}
          ></input>
        </h4>
        <br></br>
        <br></br>
        <h2>
          <input
            type="file"
            name="filename"
            onChange={(evt) => {
              setFile(evt.target.files[0]);
            }}
          ></input>
        </h2>
        <br></br>
        <br></br>
        <input
          type="submit"
          defaultValue="Submit"
          value="Submit"
          onClick={submit}
          // onClick={() => setClick(!click)}
        />
      </form>
    </div>
  );
}

export default Upload;
