import axios from "axios";
import React, { useEffect, useState } from "react";
import Modal from "react-modal";
import config from "../../config/config";

function ForgetPassword() {
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [resData, setResdata] = useState();
  const [otp, setOtp] = useState();

  // const setModalIsOpenToTrue = () => {
  //   setModalIsOpen(true);
  // };

  // const setModalIsOpenToFalse = () => {
  //   setModalIsOpen(false);
  // };

  const [email, setEmail] = useState();

  let submit = (e) => {
    e.preventDefault();
    setModalIsOpen(true);

    let Data = {
      email: email,
    };

    axios.post(config.resetApi, Data).then((response) => {
      console.log(response);
      // setGetData(response);
      setResdata(response);

    });
  };

  let check = (e) => {
    e.preventDefault();
    // setModalIsOpen(false);

    let Data = {
      email: email,
    };

    if(resData.data==otp)
    {
      axios.post(config.resetApi, Data).then((response) => {
        console.log(response);
        // setGetData(response);
        // setResdata(response);

      });
    }


  };

  return (
    <div>
      <>
        {/* <div className="popup_sec" id="pop_forgt">
          <div className="clos_btn">
            <img src="images/clos.png" alt id="clos_pop" />
          </div>
          <div className="pop_hdr">
            A mail has been send to your e-mail Id for Reset Password Link
          </div>
          <div className="man_contnt">
            <span>Please Check Your Mail Box!</span>
            <input type="submit" defaultValue="Ok" />
          </div>
        </div> */}
        {/* <div className="header"></div> */}
        {/* <div className="container">
          <div className="content"> */}
        <div className="content_rgt">
          <div className="register_sec">
            <h1>Forgot Password</h1>
            <ul>
              <li>
                <span>Enter E-mail ID</span>
                <input
                  type="text"
                  name="email"
                  onChange={(evt) => {
                    setEmail(evt.target.value);
                  }}
                />
              </li>
              <li>
                <input type="submit" defaultValue="Submit" onClick={submit} />
                <Modal isOpen={modalIsOpen} className="custom-modal-style">
                  <div className="popup_sec" id="pop_forgt">
                    <div className="clos_btn">
                      <img
                        src="images/clos.png"
                        alt
                        id="clos_pop"
                        onClick={setModalIsOpenToFalse}
                      />
                    </div>
                    <div className="pop_hdr">
                      A mail has been send to your e-mail Id for Reset Password
                      Link
                    </div>
                    <div className="man_contnt">
                      <span>Please Check Your Mail Box!</span>
                      <span>Enter Otp</span>
                <input
                  type="text"
                  name="otp"
                  onChange={(evt) => {
                    setOtp(evt.target.value);
                  }}
                />
                      <input
                        type="submit"
                        value="OK"
                        onClick={check}
                      />
                    </div>
                  </div>
                </Modal>
              </li>
            </ul>
          </div>
        </div>
        {/* </div>
        </div> */}
        <div className="clear" />
      </>
    </div>
  );
}

export default ForgetPassword;
